import React from 'react';
import {makeStyles} from "@material-ui/core/styles";
import SearchIcon from "@material-ui/icons/Search";
import SidebarChat from './SidebarChat';
import ExitToApp from "@material-ui/icons/ExitToApp";
import {IconButton} from "@material-ui/core";

export default function Sidebar()
{

    const useStyle = makeStyles((theme) =>({
        sidebar:
        {
            flex:"0.4",
            display:"flex",
            flexDirection:"column",
            borderRight:"3px solid #ecc1a3",
            boxShadow: "0 2px 4px grey",
        },
        sidebar_header:
        {
            flex:1,
            height:"80px",
            display:"flex",
            background:"#ff964c",
            boxShadow: "0 2px 4px grey",

        },
        sidebar_search_input:
        {
            display:"flex",
            alignItems:"center",
            padding:"4px",
            borderRadius:"50px",
            backgroundColor:"#f0f1f1",
            marginLeft:"5px",
            marginTop:"5px",
            marginBottom:"5px",
        },
        sidebar_search_icon:
        {
            backgroundColor:"inherit",

        },
        sidebar_search_input_field:
        {
            border:"none",
            width:"100%",
            backgroundColor:"inherit",
            outline:"none",

        },
        sidebar_chats:
        {
            overflowY:"auto"
        },
        sidebar_exit:
        {
            align:"right",
        }
    }))
    const classes=useStyle();
    return(<div className={classes.sidebar}> 
            <div className={classes.sidebar_header}>
                <div className={classes.sidebar_search_input}>
                    <SearchIcon className={classes.sidebar_search_icon}/>
                    <input type="text" className={classes.sidebar_search_input_field} placeholder="Search..."/>
                </div>

            </div>
            <div className={classes.sidebar_chats}>
            <SidebarChat/>
            <SidebarChat/>
            <SidebarChat/>
            <SidebarChat/>
