import React, { useState } from 'react';
import Sidebar from './Sidebar';
import Chat from './Chat';
import SockJsClient from "react-stomp";
import {makeStyles} from "@material-ui/core/styles";
import { Grid } from '@material-ui/core';
import Background from "../background.jpg";
import axios from 'axios';
import { onlineMembersUrl, serverWebSocketUrl, topicsUrl,loginEnd,membersUrl,offlineStatusEnd } from '../URLs';


export default function Home() 
{

  const useStyle = makeStyles((theme) =>({
    app:
    {
      display:"grid",
      placeItems:"center",
      backgroundColor:"#ffdfc9",
      height:"100vh",
      backgroundSize:"cover",
      backgroundRepeat:"no-repeat",
      backgroundImage:`URL(${Background})`,
    },
    app_body:
    {
        display:"flex",
        backgroundColor:"white",
        height:"90vh",
        width:"90vw",
        boxShadow: "-1px 4px 20px -6px rgba(0,0,0,0.2)",
    },
    }));


    const classes=useStyle();
    const me=JSON.parse(localStorage.getItem("user"));
    let [receiver,setReceiver]=useState(null);
    let [onlineMembers,setOnlineMembers]=useState([]);
    const[clientRef2,setClientRef2]=useState(null);
    let [redirectToLogin,setRedirectToLogin]=useState(false);


    function logout()
    {
       axios.get(membersUrl+me.id+offlineStatusEnd).then(console.log("you are offline now")).catch(function (error)
        {
          console.log(error);
        });
        localStorage.removeItem("user");
        sessionStorage.removeItem("emailTokenStored");
        setRedirectToLogin(true);
    }



  React.useEffect(()=>{axios.get(onlineMembersUrl).then(res=>{setOnlineMembers(res.data);console.log("hahaha"+res.data)}).catch(function (error){console.log(error)});},[]);


  if(redirectToLogin)
  {
    return <Navigate to={loginEnd}/>
  }


  return (<div className={classes.app}>
    <div className={classes.app_body}>
    <SockJsClient url={serverWebSocketUrl}
        topics={["/topic/user"]}
        onConnect={()=>{
            console.log("Connected for Online members");
            }}
        onDisconnect={()=>{console.log("Disconnected");

        }}
        onMessage={(newMember)=>{
          setOnlineMembers([...onlineMembers,newMember]);
           console.log(newMember);
        }}
        ref={(client)=>{
            setClientRef2(client);
        }}>
        </SockJsClient>
    <Sidebar setReceiver={setReceiver} onlineMembers={onlineMembers} logout={logout}/>
    <Chat receiver={receiver}/>
    </div>

  </div>

);
}
