import React, { useState } from 'react';
import {makeStyles} from "@material-ui/core/styles";
import { Avatar,IconButton,InputBase} from '@material-ui/core';
import SendIcon from "@material-ui/icons/Send";
import Message from './Message';
import ExitToApp from "@material-ui/icons/ExitToApp";
import { PermPhoneMsg, StarTwoTone } from '@material-ui/icons';
import SockJsClient from "react-stomp";
import client from 'react-stomp';
import { serverWebSocketUrl,topicsUrl } from '../URLs';

export default function Chat()
{
    const useStyle = makeStyles((theme) =>({
        chat:
        {
          flex:"0.6",
          display:"flex",
          flexDirection:"column",
          boxShadow: "0 2px 4px grey",
        },
        chat_header:
        {
            height:"60px",
            display:"flex",
            alignItems:"center",
            background:"#ff964c",
            boxShadow: "0 2px 4px grey",
        },
        chat_header_avatar:
        {
            marginLeft:"10px",
            border:"2px solid #7f1e00",
            color:"#d16519",
            background:"#ffeecb",
        },
        chat_header_name:
        {
            marginLeft:"10px",
            color:"white",
            textShadow: "2px 0 0 #7f1e00, -2px 0 0 #7f1e00, 0 2px 0 #7f1e00, 0 -2px 0 #7f1e00, 1px 1px #7f1e00, -1px -1px 0 #7f1e00, 1px -1px 0 #7f1e00, -1px 1px 0 #7f1e00",
        },
        chat_body:
        {
            padding:"30px 10px",
            overflowY:"auto",
            display:"flex",
            flexDirection:"column",

        },
        chat_footer:
        {
           display:"flex",
         //  flex:"1",
           height:"100px",
           borderTop:"2px solid grey",
        },
        chat_input:
        {
            height:"95%",
            width:"95%",

            border:"none",
            marginLeft:"10px",
            fontSize:"20px"

        },
        chat_form:
        {
            display:"flex",
            flex:"1",
            height:"100px",
        },
        button:
        {
            background:"#ffab6f",
            width:"50px",
            height:"50px",
            marginTop: "5px",
            marginBottom: "5px",
            marginLeft: "5px",
            marginRight: "5px",
        },
        chat_exit:
        {
           position:"relative",
           alignSelf:"right",
        }
    }));
    const classes=useStyle();
    let [messageText,setMessageText]=useState("");
    let [messages,setMessages]=useState([]);
    const[clientRef,setClientRef]=useState(null);

    function sendMessage()
    {
        clientRef.sendMessage('/app/user-all', JSON.stringify({  
            message: messageText
        }));
        setMessageText("");
    }

    function handleOnChange(e)
    {
        setMessageText(e.target.value);
    }
    return (
        <div className={classes.chat}>
            <div className={classes.chat_header}>
            <Avatar className={classes.chat_header_avatar}/>
            <div className={classes.chat_header_name}>
                <h2>Ime i Prezime</h2>
            </div>

            </div>
            <div className={classes.chat_body}>
            {
            messages.map(msg=> <Message txt={msg}/>)}
            </div>
            <div className={classes.chat_footer}>
                <form className={classes.chat_form}>
                <InputBase placeholder="Type a message..." className={classes.chat_input} onChange={handleOnChange} value={messageText}/>
                <IconButton className={classes.button} onMouseDown={sendMessage}><SendIcon/></IconButton>
                </form>
            </div>

        <SockJsClient url={serverWebSocketUrl}
        topics={[topicsUrl]}
        onConnect={()=>{console.log("Connected");}}
        onDisconnect={()=>{console.log("Disconnected");}}
        onMessage={(msg)=>{
            setMessages([...messages,msg]);
            console.log(msg);
        }}
        ref={(client)=>{
            setClientRef(client);
        }}>
        </SockJsClient>
        </div>
    );
}
