import Sidebar from './Sidebar';
import React from "react"
import Chat from './Chat';
import {makeStyles} from "@material-ui/core/styles";
import { Grid } from '@material-ui/core';
import Background from "../background.jpg";
import axios from "axios";
import {membersUrl,onlineStatusEnd} from "../URLs";

export default function Home() 
{

  const useStyle = makeStyles((theme) =>({
    app:
    {
      display:"grid",
      placeItems:"center",
      backgroundColor:"#ffdfc9",
      height:"100vh",
      backgroundSize:"cover",
      backgroundRepeat:"no-repeat",
      backgroundImage:`URL(${Background})`,
    },
    app_body:
    {
        display:"flex",
        backgroundColor:"white",
        height:"90vh",
        width:"90vw",
        boxShadow: "-1px 4px 20px -6px rgba(0,0,0,0.2)",
    },
    }));
    const classes=useStyle();
  const userFromStorage=JSON.parse(localStorage.getItem("user"))

  React.useEffect(()=>{axios.put(membersUrl+userFromStorage.id+onlineStatusEnd,"2").then(console.log("you are online now")).catch(function (error)
  {
    console.log(error);
  });},[]);

  return (<div className={classes.app}>
    <div className={classes.app_body}>
    <Sidebar/>
    <Chat/>
    </div>

  </div>

);
}
